using System.Collections;using System.Collections.Generic;using UnityEngine;public class Generator : MonoBehaviour {    public GameObject prefab;    public float radio = 8f;    public int n = 12;    public GameObject[] objetos;    public float torque = 50f;    public Rigidbody rb;    // Use this for initialization    void Start () {        objetos =  new GameObject[12];        CreateCarousel();    }		// Update is called once per frame	void Update () {        rb = GetComponent<Rigidbody>();        if (Input.GetAxis("Mouse ScrollWheel") > 0)        {            //transform.Rotate(new Vector3(0, speed, 0));            //GetComponent<Rigidbody>().AddTorque(360, ForceMode2D.Impulse);            rb.AddTorque(transform.up * torque );        }	}    void CreateCarousel()    {        float angle = 2 * Mathf.PI / n;        for (int i = 1; i <= n; i++)        {            Vector3 position = new Vector3(radio * Mathf.Cos(i * angle), 2f, radio * Mathf.Sin(i * angle));            var newBox = Instantiate(prefab, position, Quaternion.identity);            newBox.transform.SetParent(transform);            newBox.AddComponent<LookAt>();            newBox.gameObject.name = "Box_" + i;            objetos[i - 1] = newBox;        }    }}